sys_script_include.script:
var createNeeditRequestBody = Class.create();
createNeeditRequestBody.prototype = {
	initialize: function() {
	},
	getRestBody: function() {
		var needitGR = new GlideRecord('x_58872_needit_needit'); 
		var body =[];
		needitGR.query(); 
		while(needitGR.next()){ 
			body.push({"u_requested_for" : needitGR.getDisplayValue('u_requested_for') ,
					   "u_short_description" : needitGR.getDisplayValue('short_description')}) ;
		}
		body = JSON.stringify(body) ;
		return  body;
	},
	getSoapBody: function() {
		var xmlHeader = "<soapenv:Envelope xmlns:soapenv='http://schemas.xmlsoap.org/soap/envelope/' xmlns:x='http://www.service-now.com/x_58872_needit_add_soap_needit'><soapenv:Header/><soapenv:Body><x:insertMultiple><!--1 or more repetitions:-->" ;
		var xmlFooter = "</x:insertMultiple></soapenv:Body></soapenv:Envelope>" ;

		var needitGR = new GlideRecord('x_58872_needit_needit'); 
		//needitGR.setLimit(2) ;
		needitGR.addEncodedQuery('short_descriptionLIKEtiti');
		needitGR.query(); 
		var body =[];
		var bigbody =[];
		while(needitGR.next()){ 
			var short_desc = needitGR.getDisplayValue('short_description') ;
			short_desc =  GlideStringUtil.escapeHTML(short_desc);
			body.push({"record" :{"u_requested_for" : needitGR.getDisplayValue('u_requested_for') ,
"u_short_description" : short_desc}}) ;
		}
		bigbody.push({"R" : body}) ;

		var helper =new global.XMLHelper();
		var xmlstring = helper.toXMLStr(bigbody);
		xmlstring = xmlstring.substring(3) ;
		xmlstring = xmlstring.substring(0 ,xmlstring.length-4) ;
		var r = new RegExp('</R>', 'jg');
		xmlstring = xmlstring.replace(r, '');
		xmlstring = xmlstring.replace(/<R>/g, '');
		var soapBody = xmlHeader + xmlstring + xmlFooter ;

		return soapBody;
	},
	type: 'createNeeditRequestBody'
};
