sys_script_include.script:
var createNeeditRequestBody = Class.create();
createNeeditRequestBody.prototype = {
	initialize: function() {
	},
	getBody: function() {
		var needitGR = new GlideRecord('x_58872_needit_needit'); 
		var body =[];
		needitGR.query(); 
		while(needitGR.next()){ 
			body.push({"u_requested_for" : needitGR.getDisplayValue('u_requested_for') ,
"u_short_description" : needitGR.getDisplayValue('short_description')}) ;
		}
		body = JSON.stringify(body) ;
		return  body;
	},
	type: 'createNeeditRequestBody'
};
